<template>
	<view class="nav">
		<!-- 状态栏 -->
		<view :style="'height:' + status + 'rpx;' + containerStyle"></view>
		<!-- 顶部栏，Home页样式 -->
		<view v-if="isHome" class="headnav"
			:style="'height:'+navHeight+'rpx;line-height:'+navHeight+'rpx; padding-left: 20rpx;'">
			<text class="city">中部地区</text>
			<view style="flex:1">
				<navigator class="navigator" url="/pages/search/index" :style="navigatorStyle">
					<text class="search-text">搜索一下</text>
				</navigator>
			</view>
		</view>
		<!-- 顶部栏，其他页样式 -->
		<view v-else class="navbar" :style="'height:' + navHeight + 'rpx;' + containerStyle">
			<view class="back-icon" @click="backOrHome">
				<image v-if="pages > 1" src="@/static/image/back.png"></image>
				<image v-else src="@/static/image/home.png"></image>
			</view>
			<view class="nav-title" v-if="titleText">
				<view :style="'height:'+navHeight+'rpx;line-height:'+navHeight+'rpx;'+textStyle">{{titleText}}</view>
			</view>
		</view>
	</view>
</template>

<script setup>
	import {
		ref,
		onBeforeMount,
		defineProps,
		reactive,
		computed
	} from 'vue'
	const props = defineProps({
		background: {
			type: String,
			default: 'rgba(255,255,255,1)'
		},
		color: {
			type: String,
			default: 'rgba(0,0,0,1)'
		},
		fontSize: {
			type: [String,Number],
			default: 32
		},
		iconWidth: {
			type: [String,Number],
			default: 116
		},
		iconHeight: {
			type: [String,Number],
			default: 38
		},
		titleText: {
			type: String,
			default: ''
		},
		isHome: {
			type: Boolean,
			default: false
		}
	})
	//状态栏高度
	const status = ref(0)
	//内容栏高度
	const navHeight = ref(0)
	//背景颜色
	const containerStyle = ref('')
	//字体样式
	const textStyle = ref('')
	//图标样式
	const iconStyle = ref('')
	//获取胶囊位置
	const menu = reactive(uni.getMenuButtonBoundingClientRect?.() || {})
	//页面栈数量
	const pages = ref(getCurrentPages().length)
	// navigator样式计算
	const navigatorStyle = computed(() => {
		return {
			height: menu.height * 2 + 'rpx',
			lineHeight: menu.height * 2 + 'rpx',
			marginTop: `${menu.top * 2 - status.value}rpx`,
			marginRight: `${menu.width * 2 + 24 }rpx`, //24算是经验值，胶囊右侧边距+navigator想留的右边距
			marginLeft: '32rpx'
		}
	})
	//dom初始化前
	onBeforeMount(() => {
		setNavSize()
		setStyle()
	})
	//计算状态栏高度
	const setNavSize = () => {
		const {
			system,
			statusBarHeight
		} = uni.getSystemInfoSync()
		status.value = statusBarHeight * 2; //为什么要×2
		const isiOS = system.indexOf('iOS') > -1
		if (!isiOS) {
			navHeight.value = 96 //经验值
		} else {
			navHeight.value = 88 //经验值
		}
	}
	//样式设置
	const setStyle = () => {
		containerStyle.value = `background: ${props.background};`
		textStyle.value = `color: ${props.color}; font-size: ${props.fontSize}rpx;`
		iconStyle.value = `width: ${props.iconWidth}rpx; height: ${props.iconHeight}rpx`
	}
	// 返回或到首页
	const backOrHome = () => {
		if (pages.value > 1) {
			uni.navigateBack({
				animationType: 'auto'
			})
		} else {
			uni.switchTab({
				url: '/pages/index/index'
			})
		}
	}
</script>

<style lang="scss">
	.nav {
		position: fixed;
		width: 100%;
		top: 0;
		left: 0;
		z-index: 2;
	}

	.back-icon {
		display: flex;
		align-items: center;
		width: 64rpx;
		height: 100%;
		margin-left: 20rpx;

		image {
			height: 64rpx;
			width: 64rpx;
		}
	}

	.navbar {
		position: relative;
		// border: 1px solid blue;
		box-shadow: 0px 4px 4px 0px #00000020;
		box-sizing: border-box;

		.nav-title {
			position: absolute;
			top: 0;
			left: 50%;
			transform: translateX(-50%);
		}
	}

	.headnav {
		// border: 1px solid blue;
		box-shadow: 0px 4px 4px 0px #00000020;
		box-sizing: border-box;
		display: flex;

		.city {
			display: inline-block;
			font-size: 33rpx;
			padding-left: 55rpx;
			background-image: url("data:image/png;base64,");
			background-position-x: -10%;
			background-position-y: center;
			background-repeat: no-repeat;
			background-size: 64rpx 64rpx;
			position: relative;

			&::after {
				content: ' ';
				display: inline-block;
				height: 6px;
				width: 6px;
				border-width: 1px 1px 0 0;
				border-color: #353535;
				border-style: solid;
				-webkit-transform: matrix(0.71, 0.71, -0.71, 0.71, 0, 0);
				transform: matrix(0.71, 0.71, -0.71, 0.71, 0, 0);
				position: relative;
				top: -2px;
				position: absolute;
				top: 50%;
				margin-top: -4px;
				right: -10px;
			}
		}

		.navigator {
			background-color: #e0e0e0;
			border: 1px solid white;
			border-radius: 200rpx;
			text-align: center;

			.search-text {
				padding-left: 40rpx;
				position: relative;

				&::before {
					content: ' ';
					position: absolute;
					left: 0;
					top: 50%;
					transform: translate(0, -50%);
					height: 32rpx;
					width: 32rpx;
					background-image: url('@/static/logo.png');
					background-size: 100% 100%;
				}
			}
		}
	}
</style>